    _mTC_CDATA = []
    _mTC_TDATA = []
    def __init__(self):
        self._mTC_CDATA = list(self._TC_CDATA);
        self._mTC_TDATA = list(self._TC_TDATA);   
    def split_unicode_chrs(self,text):
      return [ chr for chr in self._unicode_chr_splitter( text ) if chr ]        
    def simple2trad(self,data):            
      mdata = self.split_unicode_chrs(data)        
      for k in range(0,len(mdata)):
        if mdata[k] in self._mTC_CDATA:
          idx = self._mTC_CDATA.index(mdata[k])
          mdata[k] = self._mTC_TDATA[idx]  
      data = "".join(mdata)
      return data
    def trad2simple(self,data):      
      mdata = self.split_unicode_chrs(data)        
      for k in range(0,len(mdata)):
        if mdata[k] in self._mTC_TDATA:
          idx = self._mTC_TDATA.index(mdata[k])
          mdata[k] = self._mTC_CDATA[idx]  
      data = "".join(mdata)
      return data          